package com.example.jcbages.happnts;

import android.content.Intent;
import android.os.Bundle;
import android.support.v7.app.AppCompatActivity;
import android.support.v7.widget.LinearLayoutManager;
import android.support.v7.widget.RecyclerView;
import android.util.Log;
import android.widget.Button;

import com.uniandes.jcbages10.routing.IMessage;
import com.uniandes.jcbages10.routing.Message;
import com.uniandes.jcbages10.routing.Routing;
import com.uniandes.jcbages10.tuplespace.ITuple;
import com.uniandes.jcbages10.tuplespace.TupleSpace;

import java.util.ArrayList;
import java.util.List;
import java.util.UUID;

public class MainActivity extends AppCompatActivity {

    private final static String TAG = MainActivity.class.getName();

    private RecyclerView mRecyclerView;
    private RecyclerView.Adapter mAdapter;
    private RecyclerView.LayoutManager mLayoutManager;

    private IPersonClickListener listener;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        this.mRecyclerView = findViewById(R.id.people_recycler_view);

        this.mLayoutManager = new LinearLayoutManager(this);
        this.mRecyclerView.setLayoutManager(mLayoutManager);

        this.listener = new PersonClickListener(this);

        this.mAdapter = new PeopleAdapter(PeopleData.people(), listener);
        this.mRecyclerView.setAdapter(this.mAdapter);
        PeopleData.setPeopleAdapter(this.mAdapter);

        publishMyself();

        restartAsyncTask();

        Button viewMatches = findViewById(R.id.view_matches_button);
        viewMatches.setOnClickListener(v -> {
            startMatchesActivity();
        });

        String profilePicBase64 = "";
        Person person = new Person("Ana", "Photographer", 22, profilePicBase64, "ana@gmail.com", 3003244592L);
        Button button = findViewById(R.id.button);
        button.setOnClickListener((v) -> {
            Log.v(TAG, "Adding tuple...");

            List<IMessage<ITuple>> messages = new ArrayList<>();

            messages.add(new Message<>(person.toTuple(UUID.randomUUID())));
            Routing.getInstance().receiveMessages(messages);
        });
    }

    private void publishMyself() {
        String profilePicBase64 = "";
        Person person = new Person("Juan", "Coder", 23, profilePicBase64, "jcbages@outlook.com", 3003244592L);
        TupleSpace.getInstance().out(person.toTuple(PeopleData.OWNER_ID));
    }

    private void startMatchesActivity() {
        Intent intent = new Intent(this, PersonMatchesActivity.class);
        startActivity(intent);
    }

    public void restartAsyncTask() {
        PeopleAsyncTask asyncTask = new PeopleAsyncTask(this);
        asyncTask.execute();
    }

    public void startDetailPersonActivity(Person person) {
        Intent intent = new Intent(this, PersonDetailActivity.class);
        intent.putExtra(Person.TAG, person);
        startActivity(intent);
    }
}
